import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';

class Album extends React.Component {
  constructor() {
    super();

    this.state = {
      musics: [],
    };
  }

  componentDidMount() {
    this.getMusicsFromAlbum();
  }

  getMusicsFromAlbum = async () => {
    const { match: { params: { id } } } = this.props; // O id é uma informação acumulada pelo próprio <Route /> que encapsula o componente React de classe "Album".

    const response = await getMusics(id);

    this.setState({ musics: response });
  }

  render() {
    const { match: { params: { id } } } = this.props;
    const { musics } = this.state;
    // artworkUrl100
    // artistName
    // collectionName
    console.log(musics)
    return (
      <div data-testid="page-album">
        <Header />
        <main>
          <section id="albumInfo">
          </section>
        </main>
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
