import React from 'react';
import PropTypes from 'prop-types';
import { addSong } from '../services/favoriteSongsAPI';

class MusicCard extends React.Component {
  constructor() {
    super();

    this.state = {
      loading: false,
    };
  }

  addRemoveFavorites = async ({ target }) => {
    const { trackInfo } = this.props;

    if (target.checked === false) {
      addSong(trackInfo);
    }
  }

  render() {
    const { trackInfo: { trackName, previewUrl, trackId } } = this.props;

    return (
      <div className="eachTrack">
        <p>{trackName}</p>
        <audio data-testid="audio-component" src={ previewUrl } controls>
          <track kind="captions" />
          O seu navegador n√£o suporta o elemento
          <code>audio</code>
        </audio>
        <label htmlFor="favCheckbox">
          Favorita
          <input
            className="favCheckbox"
            data-testid={ `checkbox-music-${trackId}` }
            type="checkbox"
            name={ trackId }
            onChange={ this.addRemoveFavorites }
          />
        </label>
      </div>
    );
  }
}

MusicCard.propTypes = {
  trackInfo: PropTypes.shape({
    trackName: PropTypes.string,
    previewUrl: PropTypes.string,
    trackId: PropTypes.number,
  }).isRequired,
};

export default MusicCard;
