import React from 'react';
// import PropTypes from 'prop-types';

class MusicCard extends React.Component {
  render() {
    const { tracksArr } = this.props;
    tracksArr.shift(); // Removi o primeiro elemento do array tendo em vista que ele não apresenta informações sobre nenhuma música, mas sim sobre o álbum.

    return (
      <section id="tracksListContainer">
        {tracksArr.map(({ trackCount, trackName, previewUrl }) => (
          <div id="eachTrack" key={ trackCount }>
            <p>{trackName}</p>
            <audio data-testid="audio-component" src={ previewUrl } controls>
              <track kind="captions" />
            </audio>
          </div>
        ))}
      </section>
    );
  }
}

export default MusicCard;
